// SPDX-License-Identifier: GPL-3.0
pragma solidity 0.8.15;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import { strings } from "./libraries/strings.sol";
import { svg } from "./svg/svg.sol";
import { svgUtils } from "./svg/svgUtils.sol";
import { SVGLibrary } from "@erc721k/periphery-sol/contracts/svg/SVGLibrary.sol";
import { SVGRegistry } from "@erc721k/periphery-sol/contracts/svg/SVGRegistry.sol";
import { Base64 } from "base64-sol/base64.sol";

contract ImpactCardRender is Ownable {
  address internal _svgLibrary;
  address internal _svgRegistry;

  string private constant ENCODING = "data:image/svg+xml;base64, ";

  mapping(uint8 => bytes) private colorFill;
  mapping(uint8 => string) private emojiFill;

  constructor(address _svgLibrary_, address _svgRegistry_) {
    _svgLibrary = _svgLibrary_;
    _svgRegistry = _svgRegistry_;
    colorFill[0] = hex"49D07D";
    colorFill[1] = hex"497FD0";
    colorFill[2] = hex"D08A49";
    // emojiFill[0] = unicode"üå±";

    emojiFill[0] = unicode"‚õ∞Ô∏è";
    emojiFill[1] = unicode"üèùÔ∏èÔ∏è";
    emojiFill[2] = unicode"üèúÔ∏è";
  }

  /* ===================================================================================== */
  /* External Functions                                                                    */
  /* ===================================================================================== */

  function render(bytes memory input) external view returns (string memory) {
    return string(abi.encodePacked(ENCODING, Base64.encode(bytes(_render(input)))));
  }

  /* ===================================================================================== */
  /* Internal Functions                                                                    */
  /* ===================================================================================== */
  function encodeSvgToDataURI(string memory data) internal view returns (string memory) {
    return string(abi.encodePacked(ENCODING, Base64.encode(bytes(data))));
  }

  function _render(bytes memory input) internal view returns (string memory) {
    (uint256 _balanceCelo, uint256 _accountNonce, uint8 cardStyle) = abi.decode(
      input,
      (uint256, uint256, uint8)
    );

    string memory _balanceCeloFormatted = string(svgUtils.round2Txt(_balanceCelo, 18, 3));
    // string memory _nonceFormatted = toString(_accountNonce);
    string memory _nonceFormatted = Strings.toString(_accountNonce);
    bytes memory _colorFill = colorFill[cardStyle];
    string memory _emojiFill = emojiFill[cardStyle];
    // bytes memory _`colorFill_ = hex"49D07D";

    return
      string.concat(
        '<svg xmlns="http://www.w3.org/2000/svg" width="500" height="300" viewBox="0 0 500 300" fill="none" style="font-family:sans-serif;" >',
        svg.rect(
          string.concat(
            svg.prop("x", "0"),
            svg.prop("width", "500"),
            svg.prop("height", "500"),
            svg.prop("transform", "rotate(90 526.995 20.9999)("),
            svg.prop("fill", string(svgUtils.getRgba(_colorFill)))
          )
        ),
        // ----------------
        // Header
        // ----------------
        svg.text(
          string.concat(
            svg.prop("x", "7.5%"),
            svg.prop("y", "15%"),
            svg.prop("font-size", "26"),
            svg.prop("font-family", "EB Garamond"),
            svg.prop("dominant-baseline", "middle"),
            svg.prop("text-anchor", "start"),
            svg.prop("fill", "white")
          ),
          "CELO"
        ),
        svg.text(
          string.concat(
            svg.prop("x", "93.5%"),
            svg.prop("y", "15%"),
            svg.prop("font-size", "22"),
            svg.prop("font-family", "EB Garamond"),
            svg.prop("font-weight", "lighter"),
            svg.prop("dominant-baseline", "middle"),
            svg.prop("text-anchor", "end"),
            svg.prop("fill", "white")
          ),
          "Climate Impact Card"
        ),
        // ----------------
        // Center
        // ----------------
        svg.text(
          string.concat(
            svg.prop("x", "50%"),
            svg.prop("y", "50%"),
            svg.prop("font-family", "unset"),
            svg.prop("font-size", "96"),
            svg.prop("dominant-baseline", "middle"),
            svg.prop("text-anchor", "middle"),
            svg.prop("fill", "white")
          ),
          _emojiFill
        ),
        // ----------------
        // Bottom
        // ----------------
        svg.g(
          string.concat(
            svg.prop("x", "2.5%"),
            svg.prop("y", "85%")
            // svg.prop('width', '500'),
            // svg.prop('height', '500'),
            // svg.prop('transform', 'rotate(90 526.995 20.9999)('),
            // svg.prop('fill', '#35D07F')
          ),
          '<svg x="2.5%" y="77.5%" width="100" height="50" fill="#FFF" id="Celo_Logotype" data-name="Celo Logotype" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 3952 1575"><defs><style>.cls-1{fill:#fff;}</style></defs><title>Artboard 1</title><path id="Bottom_Ring" data-name="Bottom Ring" class="cls-1" d="M751,1105c154.64,0,280-125.36,280-280S905.64,545,751,545,471,670.36,471,825,596.36,1105,751,1105Zm0,95c-207.1,0-375-167.9-375-375S543.9,450,751,450s375,167.9,375,375S958.1,1200,751,1200Z"/><path id="Top_Ring" data-name="Top Ring" class="cls-1" d="M951,905c154.64,0,280-125.36,280-280S1105.64,345,951,345,671,470.36,671,625,796.36,905,951,905Zm0,95c-207.1,0-375-167.9-375-375S743.9,250,951,250s375,167.9,375,375S1158.1,1000,951,1000Z"/></svg>,'
        ),
        svg.text(
          string.concat(
            svg.prop("x", "11.5%"),
            svg.prop("y", "85%"),
            svg.prop("font-size", "38"),
            svg.prop("font-family", "EB Garamond"),
            svg.prop("dominant-baseline", "middle"),
            svg.prop("text-anchor", "start"),
            svg.prop("fill", "white")
          ),
          _nonceFormatted
        ),
        svg.text(
          string.concat(
            svg.prop("x", "90%"),
            svg.prop("y", "85%"),
            svg.prop("font-size", "28"),
            svg.prop("font-family", "EB Garamond"),
            svg.prop("dominant-baseline", "middle"),
            svg.prop("text-anchor", "end"),
            svg.prop("fill", "white")
          ),
          _balanceCeloFormatted
          // _balanceCelo
        ),
        "<defs><style>.cls-1{fill:#FFF;}.cls-2{fill:#fbcc5c;}.cls-3{fill:#35d07f;}.cls-4{fill:#5ea33b;}</style></defs>",
        "</svg>"
      );
  }

  function _lib(bytes32 _key, bytes memory _value) internal view returns (string memory) {
    return SVGLibrary(_svgLibrary).execute(_key, _value);
  }

  function _registry(bytes32 _key, bytes memory _value) internal view returns (string memory) {
    return SVGRegistry(_svgRegistry).fetch(_key, _value);
  }
}

