//SPDX-License-Identifier: MIT
pragma solidity ^0.8.13;
import "@openzeppelin/contracts/utils/Strings.sol";

import { BytesLib } from "../libraries/BytesLib.sol";

/**
 * @title  SVG Utilities
 * @author Kames Geraghty
 * @notice The SVG Utilities Library provides functions for constructing SVG; format CSS and numbers.
 * @dev Original code from w1nt3r-eth/hot-chain-svg (https://github.com/w1nt3r-eth/hot-chain-svg)
 */
library svgUtils {
  using Strings for uint256;
  using Strings for uint8;

  /// @notice Empty SVG element
  string internal constant NULL = "";

  /**
   * @notice Formats a CSS variable line. Includes a semicolon for formatting.
   * @param _key User for which to calculate prize amount.
   * @param _val User for which to calculate prize amount.
   * @return string Generated CSS variable.
   */
  function setCssVar(string memory _key, string memory _val) internal pure returns (string memory) {
    return string.concat("--", _key, ":", _val, ";");
  }

  /**
   * @notice Formats getting a css variable
   * @param _key User for which to calculate prize amount.
   * @return string Generated CSS variable.
   */
  function getCssVar(string memory _key) internal pure returns (string memory) {
    return string.concat("var(--", _key, ")");
  }

  // formats getting a def URL
  function getDefURL(string memory _id) internal pure returns (string memory) {
    return string.concat("url(#", _id, ")");
  }

  // checks if two strings are equal
  function stringsEqual(string memory _a, string memory _b) internal pure returns (bool) {
    return keccak256(abi.encodePacked(_a)) == keccak256(abi.encodePacked(_b));
  }

  // returns the length of a string in characters
  function utfStringLength(string memory _str) internal pure returns (uint256 length) {
    uint256 i = 0;
    bytes memory string_rep = bytes(_str);

    while (i < string_rep.length) {
      if (string_rep[i] >> 7 == 0) i += 1;
      else if (string_rep[i] >> 5 == bytes1(uint8(0x6))) i += 2;
      else if (string_rep[i] >> 4 == bytes1(uint8(0xE))) i += 3;
      else if (string_rep[i] >> 3 == bytes1(uint8(0x1E)))
        i += 4;
        //For safety
      else i += 1;

      length++;
    }
  }

  function round2Txt(
    uint256 _value,
    uint8 _decimals,
    uint8 _prec
  ) internal pure returns (bytes memory) {
    return
      abi.encodePacked(
        (_value / 10**_decimals).toString(),
        ".",
        (_value / 10**(_decimals - _prec) - (_value / 10**(_decimals)) * 10**_prec).toString()
      );
  }

  // converts an unsigned integer to a string
  function uint2str(uint256 _i) internal pure returns (string memory _uintAsString) {
    if (_i == 0) {
      return "0";
    }
    uint256 j = _i;
    uint256 len;
    while (j != 0) {
      len++;
      j /= 10;
    }
    bytes memory bstr = new bytes(len);
    uint256 k = len;
    while (_i != 0) {
      k = k - 1;
      uint8 temp = (48 + uint8(_i - (_i / 10) * 10));
      bytes1 b1 = bytes1(temp);
      bstr[k] = b1;
      _i /= 10;
    }
    return string(bstr);
  }

  function splitAddress(address account) internal pure returns (string memory) {
    // bytes memory b = abi.encodePacked(account);
    bytes memory start = BytesLib.slice(abi.encodePacked(account), 0, 3);
    // 0x761d584f1C2d43cBc3F42ECd739701a36dFFAa31
    return string.concat("0x", string(abi.encodePacked("761d")), "...", "Aa31");
  }

  function toString(address account) internal pure returns (string memory) {
    return toString(abi.encodePacked(account));
  }

  function toString(uint256 value) internal pure returns (string memory) {
    return toString(abi.encodePacked(value));
  }

  function toString(bytes32 value) internal pure returns (string memory) {
    return toString(abi.encodePacked(value));
  }

  function toString(bytes memory data) internal pure returns (string memory) {
    bytes memory alphabet = "0123456789abcdef";

    bytes memory str = new bytes(2 + data.length * 2);
    str[0] = "0";
    str[1] = "x";
    for (uint256 i = 0; i < data.length; i++) {
      str[2 + i * 2] = alphabet[uint256(uint8(data[i] >> 4))];
      str[3 + i * 2] = alphabet[uint256(uint8(data[i] & 0x0f))];
    }
    return string(str);
  }

  function getColor(bytes memory _colorHex) internal view returns (bytes memory) {
    require(_colorHex.length == 3, "Unknown color");
    return abi.encodePacked(_colorHex, hex"64");
  }

  function getColor(bytes memory _colorHex, uint8 _alpha) internal view returns (bytes memory) {
    require(_colorHex.length == 3, "Unknown color");
    return abi.encodePacked(_colorHex, _alpha);
  }

  function getRgba(bytes memory _colorHex) internal view returns (string memory) {
    return string(toRgba(getColor(_colorHex), 0));
  }

  function toRgba(bytes memory _rgba, uint256 offset) internal pure returns (bytes memory) {
    return
      abi.encodePacked(
        "rgba(",
        byte2uint8(_rgba, offset).toString(),
        ",",
        byte2uint8(_rgba, offset + 1).toString(),
        ",",
        byte2uint8(_rgba, offset + 2).toString(),
        ",",
        byte2uint8(_rgba, offset + 3).toString(),
        "%)"
      );
  }

  function byte2uint8(bytes memory _data, uint256 _offset) internal pure returns (uint8) {
    require(_data.length > _offset, "Out of range");
    return uint8(_data[_offset]);
  }
}

@erc721k/periphery-sol/contracts/svg/SVG.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { svg } from "./svg.sol";
import { svgUtils } from "./svgUtils.sol";
import { SVGColor } from "./SVGColor.sol";

contract SVGLibrary {
  address private _colors;

  bytes32 private immutable BUILD = keccak256("BUILD");
  bytes32 private immutable COLOR = keccak256("COLOR");
  bytes32 private immutable UTILS = keccak256("UTILS");

  mapping(bytes32 => address) _modules;

  constructor(address _colors_) {
    _colors = _colors_;
  }

  function execute(bytes32 package, bytes calldata input)
    external
    view
    returns (string memory data)
  {
    if (_modules[package] != 0x0000000000000000000000000000000000000000) {
      (bool success, bytes memory data) = address(_modules[package]).staticcall(input);
      return string(data);
    } else if (package == BUILD) {
      (bool success, bytes memory data) = address(svg).staticcall(input);
      return string(data);
    } else if (package == COLOR) {
      (bool success, bytes memory data) = _colors.staticcall(input);
      return string(data);
    } else if (package == UTILS) {
      (bool success, bytes memory data) = address(svgUtils).staticcall(input);
      return string(data);
    } else {
      return string(data);
      revert("SVGLibrary:invalid-operation");
    }
  }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { ISVGModule } from "../interfaces/ISVGModule.sol";

contract SVGRegistry is Ownable {
  mapping(bytes32 => address) private _modules;

  constructor() {}

  function fetch(bytes32 widgetId, bytes memory input) external view returns (string memory) {
    if (_modules[widgetId] != address(0)) {
      return ISVGModule(_modules[widgetId]).render(input);
    } else {
      return "";
    }
  }

  function getWidget(bytes32 widgetId) external view returns (address widget) {
    return _modules[widgetId];
  }

  function setWidget(bytes32 widgetId, address widget) external onlyOwner {
    _modules[widgetId] = widget;
  }
}

