contracts/ImpactCard.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

// import { ERC721K } from "./ERC721K.sol";
import { ERC721K } from "@erc721k/core-sol/contracts/ERC721K.sol";
import { ERC721Storage } from "@erc721k/core-sol/contracts/ERC721Storage.sol";
import { ISVGRender } from "./interfaces/ISVGRender.sol";

contract ImpactCard is ERC721K {
  uint256 public nonce;
  mapping(uint256 => uint8) private styleSelection;

  constructor(
    string memory name,
    string memory symbol,
    address erc721Storage
  ) ERC721K(name, symbol, erc721Storage) {}

  receive() external payable {}

  fallback() external payable {}

  /* ===================================================================================== */
  /* External Functions                                                                    */
  /* ===================================================================================== */

  // --------------------------------------------
  // READS
  // --------------------------------------------

  function preview(bytes memory input) external view returns (string memory) {
    return ISVGRender(ERC721Storage(_erc721Storage).getSvgRender()).render(input);
  }

  // --------------------------------------------
  // WRITES
  // --------------------------------------------

  /**
   * @notice Mints a new token to the given address
   * @param to address - Address to mint to`
   */
  function mint(address to, uint8 cardStyle) external payable onlyOwner {
    unchecked {
      uint256 tokenIdNext = _idCounter++;
      styleSelection[tokenIdNext] = cardStyle;
      _mint(to, tokenIdNext);
    }
  }

  // --------------------------------------------
  // EXECTUION
  // --------------------------------------------
  function execTransaction(
    address to,
    uint256 value,
    bytes calldata data,
    uint256 eta
  ) external onlyOwner returns (bytes memory) {
    require(block.timestamp >= eta, "ImpactCard: eta not reached");
    (bool success, bytes memory returnData) = to.call{ value: value }(data);
    require(success, "ImpactCard: external call failed");
    nonce++;
    return returnData;
  }

  /* ===================================================================================== */
  /* Internal Functions                                                                    */
  /* ===================================================================================== */

  function _tokenData(uint256 tokenId)
    internal
    view
    virtual
    override
    returns (bytes memory, bytes memory)
  {
    address owner = ownerOf(tokenId);
    uint256 balanceCard = address(this).balance;
    uint256 balanceOwner = address(owner).balance;

    bytes memory imageData = abi.encode(balanceCard, nonce, styleSelection[tokenId]);
    bytes memory traitsData = bytes(abi.encode("0x0"));
    return (imageData, traitsData);
  }

  /* ===================================================================================== */
  /* Override Functions                                                                    */
  /* ===================================================================================== */
}

